// Get the timezone offset dynamically for Berlin
let berlinTime = new Date().toLocaleString("en-US", { timeZone: "Europe/Berlin", timeZoneName: "longOffset" });

// Extract the offset from the formatted string (e.g., "GMT+01:00" or "GMT+02:00")
let offsetMatch = berlinTime.match(/GMT([+-]\d{2}):(\d{2})/);

let sign = "+";
let offsetHours = "01"; // Default to Germany's winter time (UTC+1)
let offsetMinutes = "00";

// If match is found, extract the correct offset
if (offsetMatch) {
    sign = offsetMatch[1][0]; // + or -
    offsetHours = offsetMatch[1].slice(1).padStart(2, '0'); // 01 or 02
    offsetMinutes = offsetMatch[2].padStart(2, '0'); // 00
}

// Construct Berlin time in ISO format
let startDate = `${year}-${month}-${day}T${hour}:${minute}:${second}${sign}${offsetHours}:${offsetMinutes}`;

// Calculate end date (48h after start date)
let startDateObj = new Date(`${year}-${month}-${day}T${hour}:${minute}:${second}`);
startDateObj.setHours(startDateObj.getHours() + 48); // Add 48 hours

// Extract the new end date components
