let endMonth = String(startDateObj.getMonth() + 1).padStart(2, '0'); // Months are 0-based
let endDay = String(startDateObj.getDate()).padStart(2, '0');

// Construct end date in ISO format
let endDate = `${endYear}-${endMonth}-${endDay}T${hour}:${minute}:${second}${sign}${offsetHours}:${offsetMinutes}`;

// URL encode the datetime strings
msg.start_datetime = encodeURIComponent(startDate);
msg.end_datetime = encodeURIComponent(endDate);
msg.utcdate_start = startDate;
msg.utcdate_end = endDate;

return msg;

